use std::env;

// ANSI color and formatting codes
pub const RED: &str = "\033[0;31m";
pub const GREEN: &str = "\033[0;32m";
pub const YELLOW: &str = "\033[1;33m";
pub const BLUE: &str = "\033[0;34m";
pub const PURPLE: &str = "\033[0;35m";
pub const CYAN: &str = "\033[0;36m";
pub const WHITE: &str = "\033[1;37m";
pub const GRAY: &str = "\033[0;37m";
pub const RESET: &str = "\033[0m";
pub const BOLD: &str = "\033[1m";

/// Check if the terminal supports colors
pub fn should_use_colors() -> bool {
    // Check if we're in a terminal that supports colors
    env::var("NO_COLOR").is_err() && 
    env::var("TERM").map(|t| t != "dumb").unwrap_or(true) &&
    atty::is(atty::Stream::Stdout)
}

/// Apply color to text if colors are supported
pub fn colorize(text: &str, color: &str) -> String {
    if should_use_colors() {
        format!("{}{}{}", color, text, RESET)
    } else {
        text.to_string()
    }
}

/// Create a visual progress bar
pub fn create_progress_bar(percent: f64, width: usize) -> String {
    let filled = ((percent * width as f64) / 100.0) as usize;
    let empty = width - filled;

    let mut bar = String::with_capacity(width + 2);
    bar.push('[');

    if should_use_colors() {
        for _ in 0..filled {
            bar.push('â–ˆ');
        }
        for _ in 0..empty {
            bar.push('â–‘');
        }
    } else {
        for _ in 0..filled {
            bar.push('=');
        }
        for _ in 0..empty {
            bar.push('-');
        }
    }

    bar.push(']');
    bar
}

/// Get color for usage percentage
pub fn get_usage_color(percentage: f64) -> &'static str {
    if percentage < 50.0 {
        GREEN
    } else if percentage < 80.0 {
        YELLOW
    } else {
        RED
    }
}

/// Get color for time remaining
pub fn get_time_color(is_active: bool, minutes_remaining: i64) -> &'static str {
    if !is_active {
        RED
    } else if minutes_remaining < 30 {
        YELLOW
    } else {
        GREEN
    }
}

